section Section1;
shared ds_Product = let AdfDoc = AzureStorage.BlobContents("https://destoragensm.blob.core.windows.net/adf-in/Product.csv"),Csv = Csv.Document(AdfDoc, [Delimiter = ",", Encoding = TextEncoding.Utf8, QuoteStyle = QuoteStyle.Csv]), PromotedHeaders = Table.PromoteHeaders(Csv, [PromoteAllScalars = true]) in  PromotedHeaders;
shared UserQuery = let Source = #"ds_Product",
  #"Changed column type" = Table.TransformColumnTypes(Source, {{"ListPrice", Currency.Type}, {"Weight", type number}}),
  #"Added custom" = Table.AddColumn(#"Changed column type", "PricePerGm", each [Weight] / [ListPrice]),
  #"Changed column type 1" = Table.TransformColumnTypes(#"Added custom", {{"PricePerGm", type number}}),
  #"Rounded off" = Table.TransformColumns(#"Changed column type 1", {{"PricePerGm", each Number.Round(_, 5), type number}}),
  #"Added custom 1" = Table.AddColumn(#"Rounded off", "ProductNamePrefix", each Text.Start([Name], 2)) in #"Added custom 1";
